WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/33.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/33.0/apex.js")} 

var accnt = new sforce.SObject("Capital_Requests__c");
accnt.Id = '{!Capital_Requests__c.Id}';
accnt.Status__c = '{!Capital_Requests__c.Status__c}';

if(accnt.Status__c == "Closure in Process" || accnt.Status__c == "Closure Approval"){
	var r = confirm("Do you want the Closure Approval for the CFR. Are you sure ?");
	if(r == true){
		accnt.Status__c = "Closure Approval";
		var result = sforce.connection.update([accnt]);
		if(result[0].getBoolean("success")){
			window.location.reload();
		}else{
			alert("CFR is not Closure Approved. Please contact administrator.");
		}
	}
}else{
	alert("For the Closure Approval of CFR, it should be Closure in Process."); 
}
