WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/33.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/33.0/apex.js")} 

var accnt = new sforce.SObject("Capital_Requests__c"); 
accnt.Id = '{!Capital_Requests__c.Id}'; 
accnt.Status__c = '{!Capital_Requests__c.Status__c}'; 

if(accnt.Status__c == "Spend in Progress" || accnt.Status__c == "Closure in Process"){ 
	var r = confirm("Do you want move the CFR to Closure in Process. Are you sure ?"); 
	if(r == true){ 
		accnt.Status__c = "Closure in Process"; 
		var result = sforce.connection.update([accnt]); 
		if(result[0].getBoolean("success")){ 
			window.location.reload(); 
		}else{ 
			alert("CFR is not moved to Closure in Process. Please contact administrator."); 
		} 
	} 
}else{ 
	alert("For the CFR to move to Closure in Process, it should be Spend in Progress.");
}
