ValidationRule.errorConditionFormula:
OR(
  AND(
    ISBLANK( Sr_Comp_Ben_Manager__c ) ,
      NOT( ISBLANK( VP_HR_International__c ) ) 
      ) ,

AND(
    ISBLANK( HR_Manager_Director__c ) ,
    OR(
      NOT( ISBLANK( Sr_Comp_Ben_Manager__c ) ) , 
      NOT( ISBLANK( VP_HR_International__c ) ) 
        )

     )
)
